# AoC 2022 day 6

example = "mjqjpqmgbljsphdztnvjfqwrcgsmlb"


def partA(in_string):

    for i in range(0, len(in_string) - 3):
        sl = in_string[i : i + 4]
        # print(sl)
        # print(len(set(sl)))
        if len(set(sl)) == 4:
            print(f"answer: {i+4}")
            break
    return i + 4


partA(example)  # 7
partA("bvwbjplbgvbhsrlpgdmjqwftvncz")  # 5
partA("nppdvjthqldpwncqszvftbrmjlhg")  # 6
partA("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg")  # 10
partA("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw")  # 11

# actual input
actual_input = ""
partA(actual_input)
